import React  from 'react';
import ReactDOM  from 'react-dom'
import {Formik, ErrorMessage} from 'formik'
import * as yup from 'yup'
import styles from './style.module.scss';
import Button from '../../components/button'
import Select from '../../components/select'
import FormControl from '../../components/formControl'
import {createEvals,updateEvals,deleteEvals} from "../../services/firebase";
import { EvalContext } from '../../context/evals';
import { StudentContext } from '../../context/students';



const formSchema = yup.object().shape({
  student:yup.string().min(3).required(),
  class:yup.string().min(1).required(),
  date:yup.string().min(1).required(),

})

class Eval extends React.Component {
  state={
    evaluation: this.props.evaluation,
    showReload :false
  }

  componentWillReceiveProps(newProps) {
    const {
      evaluation: oldEval,
    } = this.state;
    const {
      evaluation: newEval
    } = newProps;

    if(
      newEval && oldEval &&(
        newEval.student !== oldEval.student ||
        newEval.class !== oldEval.class ||
        newEval.date !== oldEval.date 
       
      )
    ) {
        this.setState({
          evaluation: newEval,
            showReload: true,
        });
    }
}
  


onSubmit = async (values, { setSubmitting }) => {
  const {
      evaluation,
  } = this.state;

  if (evaluation) {
    await updateEvals(evaluation.id,values)
  } else {
    await createEvals(values)
  }

  setSubmitting(false);
  this.props.onClose();
}




onCancel = () =>{
  
  this.props.onClose()
}

onDelete = async()=>{
  const {
    evaluation,
    onClose
  } = this.props
  await deleteEvals(evaluation.id);
  onClose()


}

  render() {

    const{
      onClose
    }=this.props
    const {
      evaluation,
      showReload,
  } = this.state;
  const resource = evaluation || {};
  const studentsName=students.map(row =>{
    return{label: row.name,
      value: row.name}})
      const node = (
           <div className={styles.modalContainer}>
             <div className={styles.modalBox}>
              <h1 onClick={onClose}>{resource.id ? "Edit" : "Create" } Eval</h1>
              <Formik
                initialValues={{
                    student:resource.student ||'',
                    class:resource.class ||'',
                   
                    date:resource.date ||''
                    
                }}
                validationSchema={formSchema}
                onSubmit={this.onSubmit}
                render={props=>{
                    const {
                        values,
                        handleChange,
                        handleBlur,
                        handleSubmit,
                        isSubmitting,
                        handleReset,
                        isValid
                    }=props
                    return(
                        <React.Fragment>
                        <FormControl type="block">
                        <Select
                          options={studentsName}
                          value={values.student}
                          onChange={handleChange('student')}
                          onBlur={handleBlur('student')}
                          placeholder={'Student'}/>
                  <ErrorMessage  style={{ color: 'red' }} name="student"/>
                  <input type="text" className="form-control"
                  value={values.class}
                  onChange={handleChange('class')}
                  onBlur={handleBlur('class')}
                  placeholder="Class"
                  />
                  <ErrorMessage  name="Class"/>
                  
              </FormControl>
              <FormControl type="row">
              <input type="date" className="form-control"
                  value={values.date}
                  onChange={handleChange('date')}
                  onBlur={handleBlur('date')}
                  placeholder="date"
                  />
              <ErrorMessage name="date"/>

              </FormControl> 
          
                 
              <FormControl type="block" >
              <Button 
              type="primary"
              disabled={isSubmitting || !isValid}
              onClick={handleSubmit}
              label={resource.id ? 'Update'  : 'Create'}
              />
                {resource.id &&
              <Button 
              type="danger"
              onClick={this.onDelete}
              label='Delete'
              />
            }
              <Button 
              type="warning"
              
              onClick={this.onCancel}
              label='Cancel'
              />
              
            </FormControl>
              <FormControl type="row" >
            
              </FormControl>
              {showReload &&
              <FormControl>
                <div>
                  This resource has been update.
                <Button 
                type="link"
                 onClick={() =>{
                handleReset()
                this.setState({showReload:false})
              }}
              label='Click here'
              />to refresh
                </div>
              </FormControl>
              }
              </React.Fragment> 
                    )}}
              >
              
              </Formik>
             </div>
           
           </div>
    )
    return ReactDOM.createPortal(node,document.getElementById('modal-root3'))  
  }
}


Eval.contextType = EvalContext;
Eval.contextType = StudentContext;

export default Eval;
