import React from 'react';

class Select extends React.Component {
    state={
        isOpen:false
    }
    render() {
        const {
            options,
            value,
            onChange,
            onBlur,
            placheholder
        } = this.props;

        const{
            isOpen
        }=this.state
        

       return (
           <div className="container">
             <input 
             placeholder={placheholder}
            type="text"
            value={value}
            onChange={()=>{}}
            onClick={()=> this.setState({isOpen : !isOpen})}
             >
             
             </input>
             {isOpen &&
             <ul>
             {options.map(option=>
             <li 
             key={option.value}
             onClick={(e)=>{
                 onChange(option.value);
                 onBlur(e)
                 this.setState({isOpen:false})
             }
             
             }>
                 {option.label}
             </li>
                )}
            </ul>
            }
           </div>
       );
    };
}


export default Select;
