import { auth,db } from './setup'

export function watchUserChanges(callback){
const unssub = auth.onAuthStateChanged((user)=> {
    if(user && !user.isAnonymous){
       
        callback({
            id:user.uid,
            email:user.email,
            name:user.displayName
        });
    }else{
       
        callback();
    }

})
return unssub

}

export function watchStudents(callback){
    const unssub = db
         .collection('students')
         .onSnapshot((snapshot)=>{
             const docs= []
             snapshot.forEach((doc)=>{
                 const data = doc.data()
                 docs.push({
                     ...data,
                    id:doc.id,
                })
             })

             callback(docs)
         })
         return unssub

    }
