import React, { Component } from 'react';
import './App.css';
import firebase from './firebase'
import StudentForm from './components/StudentForm';
import StudentList from './components/studentList';




class Crud extends Component {
  constructor(){
    super();
    this.state ={
      user:null
    }
    this.handleAuth =this.handleAuth.bind(this)
    this.handleLogout =this.handleLogout.bind(this)
  }   

  componentWillMount(){
    firebase.auth().onAuthStateChanged(user =>{

      this.setState({user});

    })
  }
handleAuth(){
  const provider =new firebase.auth.GoogleAuthProvider()
  firebase.auth().signInWithPopup(provider)
  .then(result => console.log(`${result.user.email} ha iniciado session`))
  .catch(error =>console.log(error))
}

handleLogout(){
  console.log('anda')
  firebase.auth().signOut()
  .then(result=> console.log('Salio'))
  .catch(error=>console.log(error))
}


  render() {
    return (
    <div className="App">
      <div className="navbar navbar-ligth bg-primary">
        <a className="text-white" href="/">Tennis App</a>
      </div>
      <div className="container">

           <div className="col-md-6 m-5 ">
             <StudentForm/>


           </div>
           <div id="Students-list" className="col-md-12 mx-auto m-3">
           <StudentList/>
          </div> 
       {this.renderLoginButton()}
      </div>

    </div>
    );
  }
}

export default Crud;
