import {db} from './setup'

export async function createStudents(data){
    return await db
         .collection('students')
         .doc()
         .set(data)
}

export async function deleteStudents(id){
    return await db
         .collection('students')
         .doc(id)
         .delete()
}

export async function updateStudents(id,data){
    return await db
         .collection('students')
         .doc(id)
         .update(data)
}

export async function createGroups(data){
    return await db
         .collection('groups')
         .doc()
         .set(data)
}

export async function deleteGroups(id){
    return await db
         .collection('groups')
         .doc(id)
         .delete()
}

export async function updateGroups(id,data){
    return await db
         .collection('students')
         .doc(id)
         .update(data)
}
